using System;
using Xunit;
using Bigman.LeetCodeSolution;
using Bigman.LeetCodeSolutionTests.Utilities;

namespace Bigman.LeetCodeSolutionTests
{
    public class LongestPalindromicSubstringTest
    {
        [Theory]
        [InlineData("cbbd","bb")]
        [InlineData("babad","bab")]
        public void Test_Algorithm_one(string source, string expect)
        {
            var algorithm = new LongestPalindromicSubstring();

            var actual = algorithm.RunOne(source);

            Assert.Equal(expect, actual);
        }

        [Theory]
        [InlineData("cbbd","bb")]
        [InlineData("babad","bab")]
        [InlineData("aaaabcaaaaaa","aaaaaa")]
        [InlineData("","")]
        public void Test_Algorithm_Two(string source, string expect)
        {
            var algorithm = new LongestPalindromicSubstring();

            var actual = algorithm.RunTwo(source);

            Assert.Equal(expect, actual);
        }
    }
}